import React from "react";
import './adminManageAccount.css';
import springlogo from './test.png'
import axios from 'axios';

export default function AdminManageAccount() {
  
  const [usernameV, setUsername] = React.useState("");
  const [passwordV, setPassword] = React.useState("");
  const [roleV, setRole] = React.useState("");
  const [activeV, setActive] = React.useState("");
  
  const [output, setOutput] = React.useState("");

  //Arrow functions to handle content change
  const usernameContentChange = event => setUsername(event.target.value);
  const passwordContentChange = event => setPassword(event.target.value);
  const roleContentChange = event => setRole(event.target.value);
  const activeContentChange = event => setActive(event.target.value);

  //Arrow function to handle submit button press
  const handleSubmit = event => {
    event.preventDefault();
    setOutput("Register button pressed.")
	
	
    axios({
      method: 'post',
      url: '/createUser',
	  data: {
		  userName: usernameV,password: passwordV,roles: roleV,active: activeV
	  }
    })
	.then((response) => {
		console.log(response);
		setOutput("Sucess");
	}, (error) => {
		console.log(error);
		setOutput("Fail");
	});
	
  };

  //ToDo: Arrow function to handle pressing the Analyze button
  const handleAnalyze = event => {
    event.preventDefault();
    setOutput("Analyze button pressed.");
    
	axios({
      method: 'delete',
      url: '/analyze',
	  data: {
		  "text":usernameV
	  }
    })
	.then((response) => {
		console.log(response);
		
		var output = ""
		var stringResponse = JSON.stringify(response.data)
		var arrayResponse = stringResponse.split(',');
		
		var genderMapResponse = arrayResponse[3].split('');
		var textResponse = arrayResponse[4]
		
		//Regex to store only the user input text;it duplicates for some reason
		var textDuplicate = textResponse.match(/(?<=\:")(.*?)(?=\")/);
		//Slice duplicated text in half
		var textSlice = textDuplicate.slice(0,textDuplicate.length/2);
		//Separate words into array
		var textProto = (textSlice.toString()).split(' ');
		//Separate array with spaces between elements
		var text = textProto.join(' ');
		
		var neutral = -1 //Minus one to account for 'n' in 'genderMap'
		var masculine = 0 //Minus one to account for 'm' in 'genderMap'
		var feminine = 0 
		
		var genderMap = [];
		
		//Count number of gender-specific words, push corresponding letter to new array 
		for (var i=0; i<genderMapResponse.length;i++) 
		{
			if (genderMapResponse[i] == "n") 
			{
				neutral = neutral + 1;
				if(neutral>0)
				{
					genderMap.push("n");
				}
			}
			if (genderMapResponse[i] == "m") 
			{
				masculine = masculine + 1;
				genderMap.push("m");
			}
			if (genderMapResponse[i] == "f") 
			{
				feminine = feminine + 1;
				genderMap.push("f");
			}
		}
		
		if(feminine+masculine==0)
		{
			output = "This ad is neutral.";
		}
		
		if(masculine>feminine)
		{
			for (var i=0; i<genderMap.length;i++) 
			{
				if (genderMap[i]=="m")
				{
					//highlight text[i] in blue
					output = "text is blue";
				
				}
			}
		}	

		if(feminine>masculine)
		{
			for (var i=0; i<genderMap.length;i++) 
			{
				if (genderMap[i]=="f")
				{
					//highlight text[i] in pink
					<div className="f">text[i]</div>
				
				}
			}
		}
		

		
		setOutput(text);
	}, (error) => {
		console.log(error);
		setOutput("Fail");
	});
	
	//ToDo:
    //Need to access the content state
    //Make a POST request to /analyze with the content
    //Process the returned result.
    //If the result.status 200, analyze the returned information.
    //Else print a failure message.
    //On success, the server should return: text, countFeminine, count Masculine, genderMap.
    //If countFeminine and countMasculine are both 0, print: "This job ad is neutral"
    //Else you need to go over the genderMap to decide how to highlight 
    //which word - consider using a map function for this.
  }


  return (
    <div>
      <div className="container">
        <div className="box">
          <img src={springlogo} alt="Equality icon" className="icon" />
        </div>
		  
			<form>
				<h1>Registration form</h1>
				<p>Username:</p>
				<textarea id="ad" name="ad"
				onChange={usernameContentChange} />
				
				<p>Password:</p>
				<textarea id="ad" name="ad"
				onChange={passwordContentChange} />
				
				<p>Role:</p>
				<textarea id="ad" name="ad"
				placeholder="Example: 'ROLE_ADMIN'"
				onChange={roleContentChange}/>
				
				<p>Active:</p>
				<textarea id="ad" name="ad" 
				placeholder="Example: 'true'"
				onChange={activeContentChange}/>
				
			</form>
			
		    <form>
				<button onClick={handleSubmit}>
				Register
				</button>
			</form>
			
			<form>
				<button  onClick={handleAnalyze}>
				Analyze
				</button>
			</form>
			
	  
        
        <div className="box">
          <p>{output}</p>
        </div>
      </div>
    </div>
  );
}
